mapper.select:
SELECT DISTINCT ANAL_FIN, PROVIDER
FROM GUIDE_BOOK
WHERE 1=1
	AND ANAL_FIN = #{anal_fin}
	AND ANAL_AREA_CD = #{anal_area_cd}
ORDER BY ANAL_AREA_CD

SELECT /*+ PARALLEL(8) */ DISTINCT ROUTE_ID, ROUTE_NMA, ROUTE_TYPE, ROUTE_START, ROUTE_END 
FROM 노선별정류장간OD_주석
WHERE 1=1
AND SDATE = #{dateStart}
AND ANAL_AREA_CD = #{anal_area_cd}
AND PROVIDER = #{provider}
ORDER BY ROUTE_NMA
