AND CD_NO IN
#{arr}
ORDER BY STTN_TRAIN_SUM DESC, CD_NO ASC) WHERE R1 = 1 ORDER BY STTN_SUM DESC) WHERE ROWNUM < 101)KT WHERE 1=1 AND A.SDATE = #{dateStart} AND A.ANAL_AREA_CD = #{anal_area_cd} AND A.PROVIDER = #{provider}
AND CD_NO IN
