SELECT DISTINCT ANAL_FIN, PROVIDER
FROM GUIDE_BOOK
WHERE 1=1
	AND ANAL_FIN = #{anal_fin}
	AND ANAL_AREA_CD = #{anal_area_cd}
ORDER BY ANAL_AREA_CD

