SELECT /*+ PARALLEL(8) */ A.OPRAT_DATE, A.ANAL_AREA_CD, R, ROUTE_NMA, ROUTE_TYPE, ROUTE_START, ROUTE_END, A.CD_NO, TFCMN ,ROUTE_BUS_GIN_AGG, ROUTE_BUS_GFF_AGG, ROUTE_BUS_TRS_AGG, ROUTE_BUS_SUM FROM 결과지표_노선별통행_일별 A, (SELECT /*+ PARALLEL(8) */ R, ROUTE_ID, ROUTE_BUS_SUM FROM (SELECT /*+ PARALLEL(8) */ ROWNUM R, ROUTE_ID, ROUTE_BUS_SUM FROM (SELECT /*+ PARALLEL(8) */ ROUTE_ID ,SUM(ROUTE_BUS_GIN_AGG) OVER (PARTITION BY ROUTE_ID) + SUM(ROUTE_BUS_GFF_AGG) OVER (PARTITION BY ROUTE_ID) ROUTE_BUS_SUM ,ROW_NUMBER() OVER (PARTITION BY ROUTE_ID ORDER BY CD_NO) R1 FROM 결과지표_노선별통행_일별 WHERE 1=1 AND SDATE = #{dateStart} AND ANAL_AREA_CD = #{anal_area_cd} AND PROVIDER = #{provider}
AND CD_NO IN
#{arr}
AND TFCMN ='B' ORDER BY ROUTE_BUS_SUM DESC, CD_NO ASC) WHERE R1 = 1 ) WHERE R < 51) KT WHERE 1=1 AND A.SDATE = #{dateStart} AND A.ANAL_AREA_CD = #{anal_area_cd} AND A.PROVIDER = #{provider}
AND CD_NO IN
#{arr}
AND TFCMN ='B' AND A.ROUTE_ID = KT.ROUTE_ID ORDER BY KT.R, A.CD_NO

SELECT /*+ PARALLEL(8) */ A.OPRAT_DATE, A.ANAL_AREA_CD, KT.R, TFCMN, A.STTN_ID, STTN_NMA, STTN_HJD, A.CD_NO, TFCMN ,STTN_BUS_GIN_AGG, STTN_BUS_GFF_AGG, STTN_BUS_TRS_AGG, STTN_SUM FROM 결과지표_정류장별통행_일별 A, (SELECT /*+ PARALLEL(8) */ ROWNUM R, STTN_ID, STTN_SUM FROM (SELECT /*+ PARALLEL(8) */ STTN_ID, STTN_BUS_SUM STTN_SUM FROM (SELECT /*+ PARALLEL(8) */ STTN_ID ,SUM(STTN_BUS_GIN_AGG) OVER (PARTITION BY STTN_ID) + SUM(STTN_BUS_GFF_AGG) OVER (PARTITION BY STTN_ID) STTN_BUS_SUM ,ROW_NUMBER() OVER (PARTITION BY STTN_ID ORDER BY CD_NO) R1 FROM 결과지표_정류장별통행_일별 WHERE 1=1 AND SDATE = #{dateStart} AND ANAL_AREA_CD = '23' AND PROVIDER = #{provider}
AND CD_NO IN
#{arr}
ORDER BY STTN_BUS_SUM DESC, CD_NO ASC) WHERE R1 = 1 UNION ALL SELECT /*+ PARALLEL(8) */ STTN_ID, STTN_TRAIN_SUM STTN_SUM FROM (SELECT STTN_ID ,SUM(STTN_TRAIN_GIN_AGG) OVER (PARTITION BY STTN_ID) + SUM(STTN_TRAIN_GFF_AGG) OVER (PARTITION BY STTN_ID) STTN_TRAIN_SUM ,ROW_NUMBER() OVER (PARTITION BY STTN_ID ORDER BY CD_NO) R1 FROM 결과지표_정류장별통행_일별 WHERE 1=1 AND SDATE = #{dateStart} AND ANAL_AREA_CD = #{anal_area_cd} AND PROVIDER = #{provider}
AND CD_NO IN
#{arr}
ORDER BY STTN_TRAIN_SUM DESC, CD_NO ASC) WHERE R1 = 1 ORDER BY STTN_SUM DESC) WHERE ROWNUM < 101)KT WHERE 1=1 AND A.SDATE = #{dateStart} AND A.ANAL_AREA_CD = #{anal_area_cd} AND A.PROVIDER = #{provider}
AND CD_NO IN
#{arr}
AND A.STTN_ID = KT.STTN_ID ORDER BY KT.R, A.CD_NO
